function decodificadorMensaje($texto) {
  // Utiliza una expresión regular para encontrar todas las palabras en el texto y almacenarlas.
  preg_match_all('/\w+/', $texto, $arrayPalabras);

  // Crear un array para almacenar las palabras y sus conteos.
  $resultado = [];

  // Recorremos $arrayPalabras para validar las palabras una por una.
  foreach ($arrayPalabras[0] as $palabra) {
    // Convertir la palabra a minúsculas.
    $palabraMinuscula = strtolower($palabra);

    // Incrementar el conteo de la palabra o establecerlo en 1 si es la primera vez.
    if (array_key_exists($palabraMinuscula, $resultado)) {
      $resultado[$palabraMinuscula]++;
    } else {
      $resultado[$palabraMinuscula] = 1;
    }
  }

  $resultadoFinal = "";

  // Recorremos el array obtenido para construir la cadena del resultado.
  foreach ($resultado as $palabra => $conteo) {
    $resultadoFinal .= $palabra . $conteo;
  }

  echo $resultadoFinal;
}

